(function () {

    var EVENT_CLICK = 'click'
        , sel_self = '#item-<%= cid %>'
        , sel_title = '#title-<%= cid %>'
        , sel_description = '#description-<%= cid %>'
        , sel_image = '#image-<%= cid %>'
        , IMG_SRC = 'data:image/png;base64,'

        ;

    function PagedListItem(options) {
        var self = this;
        ly.base(this, {
            template: '/assets/lib/bootstrap/widgets/pagedlist/pagedlistitem.vhtml',
            model: false,
            view: false
        });

        self['_name'] = !!options ? options['name'] || 'name' : 'name';
        self['_description'] = !!options ? options['description'] || 'description' : 'description';
        self['_image'] = !!options ? options['image'] || 'image' : 'image';
        self['_data'] = !!options ? options['data'] || 'data' : 'data';

        // add Events
        this.on('init', _init);
    }

    ly.inherits(PagedListItem, ly.Gui);


    // ------------------------------------------------------------------------
    //                      p r i v a t e
    // ------------------------------------------------------------------------

    function _init() {
        var self = this
            ;

        if (!!self['_data']) {
            self.bindTo(_loadData)(self['_data']);
        }
    }

    function _loadData(item) {
        var self = this
            , $title = $(self.template(sel_title))
            , $description = $(self.template(sel_description))
            , $image = $(self.template(sel_image))
            ;

        $title.html(item[self['_name']]);
        $description.html(item[self['_description']]);
        $image.attr('src', item[self['_image']]||IMG_SRC);

        //-- handler --//
        $(self.template(sel_self)).on('click', function (e) {
            e.stopPropagation();
            self.trigger(EVENT_CLICK, item);
            return false;
        });
    }

    // ------------------------------------------------------------------------
    //                      e x p o r t s
    // ------------------------------------------------------------------------

    ly.provide('ly.gui.widgets.PagedListItem');
    ly.gui.widgets.PagedListItem = PagedListItem;

})();
